import './App.css';
import React, { useState } from 'react'
import {TodoCounter} from "./TodoCounter"
import {TodoSearch} from "./TodoSearch"
import {TodoList} from "./TodoList"
import {CreateButtom} from "./CreateButtom"
import {TodoItem} from "./TodoItem"


const defaulTodos=[
  {text: "salir a caminar", completed: false},
  {text: "comprar comida", completed: false},
  {text: "Estudiar mi clase", completed: true},
  {text: "Dormir", completed: true},
  {text: "Tomar Agua", completed: false}
]
function App() {
  const [searchValue, setsearchValue] = React.useState("")
  const [todos , setTodo] = React.useState(defaulTodos)

  const completedTodos = todos.filter(
    todo => !!todo.completed
  ).length;

  const totalTodos = todos.length;

  const serchedTodos = todos.filter((todo)=>{
    todo.text.includes(searchValue)
  })

  console.log(serchedTodos);

  
  
  return (
    <>
        <TodoCounter completed={completedTodos} total={totalTodos}/>
      <TodoSearch 
        searchValue={searchValue}
        setsearchValue={setsearchValue}
      />0
      <TodoList >
        {serchedTodos.map(todo=>(
          <TodoItem key={todo.text} text={todo.text} completed={todo.completed} />
        ))}
      </TodoList>
      <CreateButtom/> 
    </>
  );
}



export default App;
