import "./App.css";
import React, { useState } from "react";
import { TodoCounter } from "./TodoCounter";
import { TodoSearch } from "./TodoSearch";
import { TodoList } from "./TodoList";
import { CreateButtom } from "./CreateButtom";
import { TodoItem } from "./TodoItem";


localStorage.setItem("Todo-V1")
function App() {
  const [searchValue, setsearchValue] = React.useState("");
  const [todos, setTodo] = React.useState(defaulTodos);
  const completedTodos = todos.filter((todo) => !!todo.completed).length;
  const totalTodos = todos.length;
  const serchedTodos = todos.filter((todo) => {
     return todo.text.toLocaleLowerCase().includes(searchValue.toLocaleLowerCase());
  });

  const raeadyTodo = (text) =>{
    const newTodos = [...todos]
    const todoIndex = newTodos.findIndex(
   (todo) =>
    todo.text == text
   )
   newTodos[todoIndex].completed = true
   setTodo(newTodos)
  }

  const onDelete = (text) =>{
    const newTodos = [...todos]
    const todoIndex = newTodos.findIndex(
   (todo) =>
    todo.text == text
   )
  newTodos.splice(todoIndex, 1) 
   setTodo(newTodos)
  }

  return (
    <>
      <TodoCounter completed={completedTodos} total={totalTodos}  />
      <TodoSearch searchValue={searchValue} setsearchValue={setsearchValue} />
      <TodoList>
        {serchedTodos.map((todo) => (
          <TodoItem
            key={todo.text}
            text={todo.text}
            completed={todo.completed}
            onCompleted={()=> raeadyTodo(todo.text)}
            onDelete={()=> onDelete(todo.text)}

          />
        ))}
      </TodoList>
      <CreateButtom />
    </>
  );
}

export default App;
